create table ZG_USER
(
    ID            NUMBER                    not null,
    CNNAME        VARCHAR2(50),
    USERNAME      VARCHAR2(50) default NULL not null
        primary key,
    PASSWORD      VARCHAR2(50),
    EMAIL         VARCHAR2(50),
    TELEPHONE     VARCHAR2(50),
    MOBILE_PHONE  VARCHAR2(50),
    WECHAT_ID     VARCHAR2(50),
    SKILL         VARCHAR2(50),
    DEPARTMENT_ID VARCHAR2(50) default NULL,
    LOGIN_COUNT   NUMBER
)
/

